list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX core: <http://www.qut.edu.au/ontologies/vivoqut#>

SELECT DISTINCT ?obj ?freeTextValue1 ?freeTextValue2 ?infoType1
WHERE {
?subject ?property ?obj
OPTIONAL { ?obj core:freeTextValue1 ?freeTextValue1 }
OPTIONAL { ?obj core:freeTextValue2 ?freeTextValue2 }
OPTIONAL { ?obj core:infoType1 ?infoType1 }
}

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX core: <http://www.qut.edu.au/ontologies/vivoqut#>

CONSTRUCT {
?subject ?property ?obj .
?obj core:freeTextValue1 ?freeTextValue1 .
?obj core:freeTextValue2 ?freeTextValue2 .
?obj core:infoType1 ?infoType1 
} WHERE {
{
?subject ?property ?obj .
}
UNION {
?subject ?property ?obj .
?obj core:freeTextValue1 ?freeTextValue1
}
UNION {
?subject ?property ?obj .
?obj core:freeTextValue2 ?freeTextValue2
}
UNION {
?subject ?property ?obj .
?obj core:infoType1 ?infoType1
}
}
